import { useState } from "react";
import { useNavigate } from "react-router-dom";
import "./login.css";

const Login = () => {
    const [managerId, setManagerId] = useState(""); // Change from 'email' to 'managerId'
    const [password, setPassword] = useState("");
    const navigate = useNavigate();

    const handleLogin = (e) => {
        e.preventDefault();
        console.log("Logging in with Manager ID:", managerId, "Password:", password);
        navigate("/EmployeeForm"); // Redirect after login
    };

    return (
        <div className="login-container">
            <div className="login-box">
                <h2>Login</h2>
                <form onSubmit={handleLogin}>
                    <input
                        type="text"
                        placeholder="Manager ID (4 digits)"
                        value={managerId}
                        onChange={(e) => setManagerId(e.target.value)}
                        required
                        maxLength="4"
                        minLength="4"
                        pattern="\d{4}"
                        className="login-input"
                    />
                    <input
                        type="password"
                        placeholder="Password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                        className="login-input"
                    />
                    <button type="submit" className="login-button">Login</button>
                </form>
                <p>
                    Don't have an account? <a href="/signup" className="signup-link">Sign up</a>
                </p>
            </div>
        </div>
    );
};

export default Login;
